    private static void GetFishingPondState(
      int x,
      int y,
      out bool lava,
      out bool honey,
      out int numWaters,
      out int chumCount)
    {
      lava = false;
      honey = false;
      numWaters = 0;
      chumCount = 0;
      Point tileCoords = new Point(0, 0);
      int minX;
      int maxX;
      Projectile.GetFishingPondWidth(x, y, out minX, out maxX);
      for (int i = minX; i <= maxX; ++i)
      {
        int j = y;
        while (Main.tile[i, j].liquid > (byte) 0 && !WorldGen.SolidTile(i, j, false) && j < Main.maxTilesY - 10)
        {
          ++numWaters;
          ++j;
          if (Main.tile[i, j].lava())
            lava = true;
          else if (Main.tile[i, j].honey())
            honey = true;
          tileCoords.X = i;
          tileCoords.Y = j;
          chumCount += Main.instance.ChumBucketProjectileHelper.GetChumsInLocation(tileCoords);
        }
      }
      if (!honey)
        return;
      numWaters = (int) ((double) numWaters * 1.5);
    }

    private static void GetFishingPondWidth(int x, int y, out int minX, out int maxX)
    {
      minX = x;
      maxX = x;
      while (minX > 10 && Main.tile[minX, y].liquid > (byte) 0 && !WorldGen.SolidTile(minX, y, false))
        --minX;
      while (maxX < Main.maxTilesX - 10 && Main.tile[maxX, y].liquid > (byte) 0 && !WorldGen.SolidTile(maxX, y, false))
        ++maxX;
    }

